//Importacion

import React, {useState} from 'react'
import Result from './components/Result'
import Functions from './components/Functions'
import Numbers from './components/Numbers'
import MathOperations from './components/MathOperations'
import './App.css'

//Genracion de la funcion del componente

const App = () => {
    const arrayTextoFuncionModificaTexto= useState("")
    const texto=arrayTextoFuncionModificaTexto[0]
    const funcionModificaTexto = arrayTextoFuncionModificaTexto[1]
    
    //cuerpo de la funcion
    console.log("Renderizacion de App")
    return(<main className='react-calculator'>
        <Result value ={texto} />
        <Numbers onClickNumber={number => {
            console.log("Click en Number:",number)
            funcionModificaTexto(number)
        }}></Numbers>
        <Functions onContentClear={() => {console.log("Content Clear")}}
         onDelete={() => console.log("On Delete")}>
        </Functions>
        <MathOperations 
        onClickOperation= {operation => console.log("Operation:", operation)}
        onClickEqual ={equal => console.log("Equal:",equal)}>
            </MathOperations>
    </main>)
}

//Exportacion

export default App
